item_prototype.params:
SELECT tablespace_size*{#BSIZE} AS ts_size
FROM dba_tablespace_usage_metrics
WHERE tablespace_name='{#TSNAME}';

SELECT used_space*{#BSIZE} AS used_space
FROM dba_tablespace_usage_metrics
WHERE tablespace_name='{#TSNAME}';

SELECT ROUND(used_percent,2) AS used_pct
FROM dba_tablespace_usage_metrics
WHERE tablespace_name='{#TSNAME}';
